select 

from SLR_SHOP


    and SELLER_ID = #{sellerId,jdbcType=BIGINT}


    and SHOP_NAME = #{shopName,jdbcType=VARCHAR}


    and SHORT_NAME = #{shortName,jdbcType=VARCHAR}


    and ADDERSS = #{adderss,jdbcType=VARCHAR}


    and LONGITUDE = #{longitude,jdbcType=VARCHAR}


    and LATITUDE = #{latitude,jdbcType=VARCHAR}


    and CONTACT = #{contact,jdbcType=VARCHAR}


    and IS_ENABLED = #{isEnabled,jdbcType=TINYINT}


    and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


    and MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}


select count(*) 
from SLR_SHOP
where ID = #{id,jdbcType=BIGINT}


